// Parse Altium Netlist (.NET file)
const fileData = $input.first().binary.data;
const content = Buffer.from(fileData, 'base64').toString('utf-8');

const lines = content.split('\n').map(line => line.trim()).filter(line => line.length > 0);

const components = [];
const netConnections = [];

let currentSection = '';
let currentNet = null;
let currentNetId = null;

for (let i = 0; i < lines.length; i++) {
  const line = lines[i];

  // Detect sections
  if (line === '<<< Component List >>>') {
    currentSection = 'components';
    continue;
  }
  if (line === '<<< Wire List >>>') {
    currentSection = 'wires';
    continue;
  }

  // Skip headers and empty lines
  if (line.startsWith('NODE') || line.startsWith('Wire List') || line === '') {
    continue;
  }

  if (currentSection === 'components') {
    // Parse component line: PART_NUMBER DESIGNATOR FOOTPRINT
    const parts = line.split(/\s+/);
    if (parts.length >= 3) {
      components.push({
        component_type: 'component',
        part_number: parts[0],
        designator: parts[1], 
        footprint: parts[2],
        value: parts[0] // Using part number as value for now
      });
    }
  }

  if (currentSection === 'wires') {
    // Parse net header: [NETID] NET_NAME
    const netMatch = line.match(/^\[(\d+)\]\s+(.+)$/);
    if (netMatch) {
      currentNetId = netMatch[1];
      currentNet = netMatch[2];
      continue;
    }

    // Parse connection line: DESIGNATOR PIN_NUM PIN_NAME PIN_TYPE PART_VALUE
    if (currentNet && line.includes('  ')) {
      const parts = line.split(/\s+/).filter(p => p.length > 0);
      if (parts.length >= 5) {
        netConnections.push({
          component_type: 'net_connection',
          net_name: currentNet,
          net_id: currentNetId,
          designator: parts[0],
          pin_number: parts[1],
          pin_name: parts[2],
          pin_type: parts[3],
          part_value: parts.slice(4).join(' ')
        });
      }
    }
  }
}

// Combine all data
const allData = [...components, ...netConnections];

return allData.map(item => ({ json: item }));

